import "./App.css";
import React, { useState } from "react";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
// import share btns
import {
  FacebookShareButton,
  TwitterShareButton,
  FacebookIcon,
EmailShareButton ,
EmailIcon
} from "react-share";

const App = () => {
  const url = "https://api.quotable.io/random";
  let quoteData = {
    content: "Let time be your only competitor.",
    author: "Ahmed Saber"
  };
  const [quote, setQuote] = useState(quoteData);

  const generateQuote = () => {
    fetch(url)
      .then(response => response.json())
      .then(data => {
        console.log(data);
        setQuote(data);
      });
  };

  const copy = () => {
    navigator.clipboard.writeText(
      quote.author + " once said: " + quote.content
    );
    // alert('copied')
    // instead of alert built in browser we will customize it

    toast.info("Copied", {
      position: toast.POSITION.TOP_CENTER
    });
  };

  return (
    <>
      <h1>Quote Generator React App</h1>
      <div className="container">
        <p>{quote.content}</p>
        <span>{quote.author}</span>
        <div className="btns">
          <button onClick={copy} className="btn">
            Copy
          </button>
          <button>share</button>
          <button onClick={generateQuote}>Generate Another Quote</button>
        </div>
      </div>
      <div className="shareContainer">
        <FacebookShareButton url="https://api.quotable.io/random" quote="ahmed">
          <FacebookIcon size="40" />
        </FacebookShareButton>
        <LinkedInShareButton  url="https://api.quotable.io/random" quote="ahmed">
          <LinkedinIcon />
        </LinkedInShareButton>
      </div>
      <ToastContainer />
    </>
  );
};

export default App;
